#include <stdio.h>

int main()
{
    printf("CHUONG TRINH TINH CHU VI DIEN TICH HINH CHU NHAT");
    printf("\n************************************************");

    float a,b;
    printf("\nMoi ban nhap canh A : ");
    scanf("%f",&a);
    printf("\nMoi ban nhap canh B : ");
    scanf("%f",&b);
    printf("\nChu vi hinh chu nhat la: %f",a+b);
    printf("\nDien tich hinh chu nhat la: %f",a*b);

}